import React from 'react';

class FoodNews extends React.Component{
    constructor(props){
        super(props);
    }
    render(){
        let {title,urlToImage,description,url}=this.props.data;
        return(
        <article className="news">
        <div className="news__item">
          <h2 className="news__header">Food News</h2>
          <a 
            className="news__title" 
                href={url}>{title}</a>
          <img 
            className="news__image"
            src={urlToImage} alt='' height={100} width={200}
            />
          <p className="news_description">{description}</p>
        </div>
        </article>
        )
    }
}

export default FoodNews;
