import React,{useState,useEffect} from 'react';
import axios from 'axios';

export default function SearchBar() {
    //STATES 
    const [searchInput,setSearchInput]=useState(null)
    const [resultData,setResultData]=useState([])

    //HANDLERS
    function handleInput(e){
        setSearchInput(e.target.value)

        if(e.target.value===''){
            setResultData([])
        }
    }
    function handleBlur(e){
        e.target.value=''
        setSearchInput('')
        setResultData([])
    }


    //USE EFFECT 
    useEffect(()=>{
        if(searchInput===null) return;
        axios.post('http://localhost:3000/searchbar',{input:searchInput})
        .then(data=>setResultData(data.data))
    },[searchInput])



    //MAPPING RENDER
    const renderList=resultData.map(result=>
            <li className="searchbar__resultItem" key={result.id}>
            {result.title}</li>
        );
    //RENDERING
    return(
    <div className="searchbar">

        <form className="searchbar__form" autoComplete="off">
            <input id='search'
            className="searchbar__input" placeholder='search flamin 
            recipes...'
            onChange={handleInput} onBlur={handleBlur}></input>

            <button className="searchbar__button" type="submit" htmlFor='search'><i className="searchbar__icon md-dark">search</i>
            </button>
        </form>
        <ul className="searchbar__resultList" id="searchbar__results">
           {renderList}
        </ul>

    </div>
    )
}
