import React from 'react';
import './App.css';
import FoodNews from './components/FoodNews'

class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      newsFeed:{},
      dailyInspirations:{},
      highestRatedRecipes:{},
      trendingRecipes:{}
    }
  }
  componentDidMount(){
    fetch('http://localhost:3000/news').then(res=>res.json()).then(data=>{
        this.setState({newsFeed:data})
      })
  }

  render(){
  if(this.state.newsFeed.author){
    let sourceName=this.state.newsFeed.source.name;

   return (
    <div className="App">
    <nav className="navbar">
        <h1 className="navbar__website__title">FlavorTown</h1>
        <form className="searchbar__form">
          <label className="searchbar__label" for='search'>Search fire recipes:</label>
          <input id='search'className="searchbar__input" placeholder='search' value='search'></input>

          <button className="searchbar__button" type="submit"for='search'><i className="material-icons md-dark">face</i>
          </button>
        </form>
        <a className="navbar__link" href='google.com' alt=''>Favorites</a>
        <a className="navbar__link"  href='google.com' alt=''>Wishlist</a>
        <a className="navbar__link"  href='google.com' alt=''>Recipes</a>
    </nav>
    <main className='main'>
      <section className="main__banner">
          <FoodNews
            data={this.state.newsFeed}
            source={sourceName}
          />

        <aside className='trending'>
          <h2 className="trending__header">Highest-Rated</h2>
        </aside>
        <aside className="highestRated">
          <h2 className="highestRated__header">Trending</h2>
        </aside>
      </section>
      <section className="main__fieri">
        <h2>The Daily Fieri</h2>
        <p>Our lord and savior</p>

      </section>
  </main>
    </div>
  )
  }
  else{
    return(<div></div>)
  }
}
}

export default App;
