import React,{useState,useEffect} from 'react';

export default function MainFoodArticle (){
  const [displayData,setDisplayData]=useState(null);

  useEffect(()=>{
    fetch('https://desolate-tor-39614.herokuapp.com/news')
    .then(res=>res.json())
    .then(data=>{setDisplayData(data)})

  },[])

  if(!displayData){
    return(
      <article className="news">
      <h2 className="news__type">Food News</h2>
      <div className="news__item">
        <header className="news__header">

              <img 
                    className="news__image"
                    src='' alt='' height={100} width={175}
                />
          <summary className="news__description"></summary>
        </header>
        <footer>
                  <p className="news__author">"company" 
                </p>
         </footer>
        </div>
      </article>
    )
  }

  if(displayData){
    const {title,urlToImage,description,url,author,source}=displayData;
  return(
    <article className="news">
      <h2 className="news__type">Food News</h2>
      <div className="news__item">
        <header className="news__header">
        <a className="news__title" href={url}>      {title}</a>
              <img 
                    className="news__image"
                    src={urlToImage} alt='' height={100} width={175}
                />
          <summary className="news__description">{description}</summary>
        </header>
        <footer>
                  <p className="news__author">{`By:${author}, ${source}`} 
                </p>
         </footer>
        </div>
      </article>
  )}
}

