import React,{useState,useEffect } from 'react';
import axios from 'axios';

export default function Highest(){
    //STATE 
    const [displayData,setDisplayData]=useState([]);

    //EFFECTS 
    useEffect(()=>{
      fetch(process.env.REACT_APP_API_ADDRESS+'/highest')
        .then(res=>res.json())
        .then(data=>{
          console.log(data)
          setDisplayData(data)
        })
    },[])

    //HANDLERS 
    function handleClick(id){
        console.log("handleClick",id)
      axios.post(process.env.REACT_APP_API_ADDRESS+'/trending/handleclick',{id}).then(data=>window.open(data.data.sourceUrl,"_blank"))
    }

  //METHODS
    const renderHighest=displayData.map(recipe=>
      <li >
          <a href={recipe.sourceUrl} target='_blank' rel='noopener noreferrer' alt={'hyperlink to'+recipe.title}>{recipe.title}</a>
          </li>
      )


    //RENDERING 
    return(
        <aside className="list">
          <h2 className="list__header">
             Highest-Rated Recipes
          </h2>
          <ol className="list__list">
                {renderHighest}
            </ol>
        </aside>
        )
    }
