import React from 'react';

class FoodNews extends React.Component{
    constructor(props){
        super(props);
    }
    componentDidMount(){
        this.setState({
            data:this.props.data,
        })
        console.log(this.state.data);
    }
    render(){
        let {title,urlToImage,description,url,author,source}=this.props.data;
        if(!source.name){
           return( <article className="news"></article>)
        }

        else{

        return(
        <article className="news">
        <div className="news__item">
          <h2 className="news__header">Food News</h2>
          <header>
            <a 
            className="news__title" 
                href={url}><h3>{title}</h3></a>
             <img 
            className="news__image"
            src={urlToImage} alt='' height={100} width={200}
            />

            <p className="news_description">{description}</p>
          </header>
          <footer>
              <p>{`By:${author}`}</p>
          </footer>
        </div>
        </article>
        )
    }
    }
}

export default FoodNews;
