import React,{useState,useEffect} from 'react';

export default function Trending (){
    //STATE
    const [displayData,setDisplayData]=useState(null);

    //EFFECTS 
    useEffect(()=>{
        fetch('http://localhost:3000/trending')
        .then(res=>res.json())
        .then(data=>{setDisplayData(data)})
      },[])


    //METHODS
    const recipes=()=>{
       return displayData.map(recipe=>{
            return(
                <li className="trending__recipe">
                    <a 
                    href={recipe.source_url} 
                    className="trending__link"
                    >
                         {recipe.title}
                    </a>
                </li>
            )
        })
    }

     return( 
        <aside className='trending'>
        <h2 className="trending__header">Trending</h2>
        <ol className="trending__list">
            {recipes?recipes:"Loading..."}
        </ol>
      </aside>
     )
}
