  constructor(props){
    super(props);
    this.state={
      newsFeed:{},
      trendingRecipes:[],
      highestRecipes:[]
    }
  }
  componentDidMount(){
        let p1=fetch('http://localhost:3000/news')
        let p2=fetch('http://localhost:3000/trending')
        let p3=fetch('http://localhost:3000/highest')
        let result;
    return Promise.all([p1,p2,p3]).then(values=>console.log(values))

    //fetch news
