    //     this.setState({trendingRecipes:data})
    //     console.log('FETCH TRENDING')
    //   })
    // //fetch highest 
    // fetch('http://localhost:3000/highest')
    // .then(res=>res.json())
    // .then(data=>{
    //   this.setState({highestRecipes:data})
    //   console.log('FETCH HIGHEST')
    // })

  }

  render(){
    let {newsFeed,highestRecipes,trendingRecipes}=this.state;

  if(newsFeed.title&&highestRecipes.title&&trendingRecipes.title){
      let sourceName=this.state.newsFeed.source.name;
   return (
    <div className="App">
    <nav className="navbar">
        <h1 className="navbar__website__title">FlavorTown</h1>
        <form className="searchbar__form">
          <label className="searchbar__label" htmlFor='search'>Flamin' Recipes:</label>
          <input id='search'className="searchbar__input" placeholder='search...'></input>

          <button className="searchbar__button" type="submit" htmlFor='search'><i className="searchbar__icon md-dark">search</i>
          </button>
        </form>
        <a className="navbar__link" href='google.com' alt=''>Favorites</a>
        <a className="navbar__link"  href='google.com' alt=''>Wishlist</a>
        <a className="navbar__link"  href='google.com' alt=''>Recipes</a>
    </nav>


    <main className='main'>
      <section className="main__banner">

          <FoodNews
            data={this.state.newsFeed}
            source={sourceName}
          />

          <Trending />

          <Highest />

      </section>

  </main>
    </div>
  )
  }
  else{
    return(<div> Rendered but not Loaded</div>)
  }
}
}

export default App;
