import React,{useState,useEffect } from 'react';

export default function Highest(){
    //STATE 
    const [displayData,setDisplayData]=useState(null);

    //EFFECTS 
    useEffect(()=>{
        fetch('https://desolate-tor-39614.herokuapp.com/highest')
        .then(res=>res.json())
        .then(data=>setDisplayData(data))
    },[])

    //MAP DATA ARRAY 
    let recipes=displayData.map(recipe=>{
            return(
                <li className="highest__recipe">
                    <a 
                    href={recipe.source_url} 
                    className="highest__link"
                    >
                         {recipe.title}
                    </a>
                </li>
                )
            }
        )
    //RENDERING 
        return(
        <aside className="highestRated">
          <h2 className="highestRated__header">
             Highest-Rated
          </h2>
          <ol className="highest__list">
                {recipes?recipes:null}
            </ol>
        </aside>
        )
    }
