import React from 'react';
import './App.css';
import FoodNews from './components/FoodNews'

class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      newsFeed:{},
      dailyInspirations:{},
      highestRatedRecipes:{},
      trendingRecipes:{}
    }
  }
  componentWillMount(){
    fetch('http://localhost:3000/news').then(res=>res.json()).then(data=>{
        this.setState({newsFeed:data})
      })
  }q

  render(){
  return (
    <div className="App">
    <nav>
        <h1 className="nav__website__title">FlavorTown</h1>
        <input placeholder='search'></input>
        <a href='google.com' alt=''>Favorites</a>
        <a href='google.com' alt=''>Wishlist</a>
        <a href='google.com' alt=''>Recipes</a>
    </nav>
    <main className='main'>
      <section className="main__banner">
          <FoodNews
            data={this.state.newsFeed}
          />

        <aside className='trending'>
          <h2 className="trending__header">Highest-Rated Recipes</h2>
        </aside>
        <aside className="highestRated">
          <h2 className="highestRated__header">Trending Recipes</h2>
        </aside>
      </section>
  </main>
    </div>
  )
  }
}

export default App;
