import React,{useState,useEffect} from 'react';


export default function Trending (){
    //STATE
    const [displayData,setDisplayData]=useState(null);

    //EFFECTS 
    useEffect(()=>{
        fetch('http://localhost:3000/trending')
        .then(res=>res.json())
        .then(data=>{
            setDisplayData(data)
        })
      },[])


    //METHODS
      const renderList=displayData.map(recipe=>
        <li className="highest__recipe">
            {recipe.title}
            </li>
        )



     return( 
        <aside className='trending'>
        <h2 className="trending__header">Trending</h2>
        <ol className="trending__list">
            {renderList?renderList:"Loading..."}
        </ol>
      </aside>
     )
}
