import React from 'react';
import './App.css';
import FoodNews from './components/FoodNews';
import Trending from './components/Trending';
import Highest from './components/Highest';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      newsFeed:{},
      trendingRecipes:[],
      highestRecipes:[]
    }
  }
  componentDidMount(){
        let p1=fetch('http://localhost:3000/news')
        let p2=fetch('http://localhost:3000/trending')
        let p3=fetch('http://localhost:3000/highest')

    Promise.all([p1,p2,p3])
    .then(
      ([res1,res2,res3])=>{
        Promise.all([
          res1.json(),
          res2.json(),
          res3.json()
        ])
        .then(([data1,data2,data3])=>{
          this.setState({newsFeed:data1});
          this.setState({trendingRecipes:data2});
          this.setState({highestRecipes:data3});
          console.log("data1",data1);
          console.log("data2",data2);
          console.log("data3",data3)
        })
      }
    )
  }

  render(){

  if(this.state.newsFeed.author){
      let sourceName=this.state.newsFeed.source.name;
   return (
    <div className="App">
    <nav className="navbar">
        <h1 className="navbar__website__title">FlavorTown</h1>

        <a className="navbar__link" href='google.com' alt=''>Favorites</a>
        <a className="navbar__link"  href='google.com' alt=''>Wishlist</a>
        <a className="navbar__link"  href='google.com' alt=''>Recipes</a>
    </nav>
    <form className="searchbar__form">
          <label className="searchbar__label" htmlFor='search'>Search Flamin' Recipes:</label>
          <input id='search'className="searchbar__input" placeholder='search...'></input>

          <button className="searchbar__button" type="submit" htmlFor='search'><i className="searchbar__icon md-dark">search</i>
          </button>
      </form>

    <main className='main'>
      <section className="main__banner">

          <FoodNews
            data={this.state.newsFeed}
            source={sourceName}
          />

          <Trending />

          <Highest />

      </section>

  </main>
    </div>
  )
  }
  else{
    return(<div> Rendered but not Loaded</div>)
  }
}
}

export default App;
