import React,{useState,useEffect} from 'react';

export default function GuyNews(){
    //STATE 
    const [displayData,setDisplayData]=useState([]);
    const [carouselPosition,setCarouselPosition]=useState(0);

    //GET API 
    useEffect(()=>{
        fetch('https://flavortown-api.herokuapp.com/carousel')
        .then(res=>res.json())
        .then(data=>setDisplayData(data))
    },[])

    //Rendering for Carousel


   //HANDLERS
    function handleBack(){
        if(carouselPosition===0){
            setCarouselPosition(displayData.length-1)
        }
        else{
            setCarouselPosition(carouselPosition-1)
        }
    }
    function handleForward(){
        if(carouselPosition===displayData.length){
            setCarouselPosition(0)
        }else{
            setCarouselPosition(carouselPosition+1)
        }
    }

    //RENDERING
    //

    //if loaded 
    if(displayData[0]){
        console.log('displayData mounted')
        function Card({title,urlToImage,description,url,author,source}){
            return(
                <div className="guyNews__card">
                <a className="guyNews__cardTitle" href={url}>{title}</a>
                        <img 
                            className="guyNews__cardImage"
                            src={urlToImage} alt='' height={100} width={175}
                        />
                        <summary className="news__cardDescription">{description}</summary>

                            <p className="news__cardAuthor">{`By:${author}, ${source.name}`} 
                        </p>
            </div>
            )
        }




        return(
            <>
            <div className="guyNews">
            <h2 className="guyNews__title">Guy Fieri News</h2>
            <section className='guyNews__body'>
            <button className="guyNews__button" onClick={()=>handleBack()}> {"<"} </button>

            <article className="guyNews__article">
               {displayData.map((card,index)=>{
                const {title,urlToImage,url,author,source}=card;
                return(
                    <Card
                        key={index}
                        title={title}
                        urlToImage={urlToImage}
                        url={url}
                        author={author}
                        source={source}
                    />
                )
            })}
            </article>

            <button className="guyNews__button" onClick={()=>handleForward()}  > {">"} </button>
            </section>
            </div>

            </>
        )
    }
    //defaut return if nothing is loaded 

    return(
        <div>  Loading ... </div>
    )

}
