import React from 'react';
import './App.css';
import FoodNews from './components/FoodNews'

class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      newsFeed:{},
      dailyInspirations:{},
      highestRatedRecipes:{},
      trendingRecipes:{}
    }
  }
  componentWillMount(){
    fetch('http://localhost:3000/news').then(res=>res.json()).then(data=>this.setState({newsFeed:data}))
  }q

  render(){
    console.log(this.state.newsFeed)
  return (
    <div className="App">
    <nav>
        <h1>FlavorTown</h1>
        <input placeholder='search'></input>
        <a>Favorites</a>
        <a>Wishlist</a>
        <a>Recipes</a>
    </nav>
    <main>
      <section className="section">
        <FoodNews
          data={this.state.newsFeed}
         />
        <div className="section__item">
          <h2>Daily Inspirations</h2>
        </div>
      </section>
      <aside className='trending'>
        <h2>Highest-Rated Recipes</h2>
      </aside>
      <aside className="highestRated">
        <h2>Trending Recipes</h2>
      </aside>
  </main>
    </div>
  )
  }
}

export default App;
