import React,{useState,useEffect} from 'react';

export default function Random () {
    //STATE
    const [displayData,setDisplayData]=useState(null);


    //EFFECTS 
    useEffect(()=>{
        fetch('http://localhost:3000/random')
        .then(res=>res.json()).then(data=>setDisplayData(data))
        }
        ,[])

    //RENDERING



    if(!displayData){
        return(
        <div>Loading..</div>
        )
    }
    if(displayData){
        const {cookingMinutes,extendedIngredients, image, instructions, title,sourceUrl}=displayData;
        const renderIngredients= extendedIngredients.map(ingredient=>console.log(ingredient));

    return(
        <div className="random" >
           <a href={sourceUrl} ><h1>Recipe: {title}</h1></a>
            <img src={image} alt=''/>
            <section>
                <h2>Instructions</h2>
                <p>{instructions}</p>

            </section>
        </div>
        )

    }
}
